import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import profilePic from '../images/Sebas-dog.jpeg'

const User = () => {

    const [posts, setPosts] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const res = await axios.get('/posts')
                setPosts(res.data)
            } catch (err) {
                console.log(err)
            }
        }
        fetchData();
    }, [])

    const userEdit = () => {
        const [showEdit, setShowEdit] = useState(false);

        const handleClick = () => {
            setShowEdit(true);

        }

        const Edit = () => {
            <button>hello</button>
        }
    }





    const getText = (html) => {
        const doc = new DOMParser().parseFromString(html, "text/html")
        return doc.body.textContent
    }



    return (
        <div className='user-page'>
            <div className='user-posts'>
                {posts.map((post) => (
                    <div className='user-post' key={post.id}>
                        <div className='post-img'>
                            <img src={`../upload/${post.img}`} alt="" />
                        </div>
                        <div className='post-content'>
                            <Link className='link' to={`/post/${post.id}`}>
                                <h1>{getText(post.title)}</h1>
                            </Link>
                            <p>{getText(post.desc)}</p>
                            <Link className='link' to={`/post/${post.id}`}>
                                <button va>ReadMore</button>
                            </Link>
                        </div>
                    </div>
                ))}
            </div>
            <div className='user-info'>
                <div className='profile-container'>
                    <img className='profile-img' src={profilePic} />
                    <div className="profile-info">
                        <h3>Sebastain Tischner</h3>
                        <h5>Username: Sebascrab</h5>
                        <h5>alexanders.tischner@outlook.com</h5>
                        <button onClick={handleClick}>{showEdit ? <Edit /> : null}Edit</button>
                    </div>
                </div>
            </div>

        </div>
    )
}

export default User
